    public class DupdobDay1
    {

        public int ComputeDay1(string input = Input)
        {
            var floor = 0;
            foreach (var letter in input)
            {
                switch (letter)
                {
                    case '(':
                        floor++;
                        break;
                    case ')':
                        floor--;
                        break;
                }
            }

            return floor;
        }
        
        public int ComputeDay2(string input = Input)
        {
            var floor = 0;
            var move = 1;
            foreach (var letter in input)
            {
                switch (letter)
                {
                    case '(':
                        floor++;
                        break;
                    case ')':
                        floor--;
                        break;
                }

                if (floor == -1)
                    return move;
                move++;
            }

            return -1;
        }

